<?php

class MS_Rule_ReplaceMenu_View extends MS_View {

	public function to_html() {
		$membership = $this->data['membership'];

		// This fixes the list-title generated by MS_Helper_ListTable_Rule.
		unset( $_GET['status'] );

		$rule = $membership->get_rule( MS_Rule_ReplaceMenu::RULE_ID );
		$listtable = new MS_Rule_ReplaceMenu_ListTable(
			$rule,
			$membership
		);

		$listtable->prepare_items();

		$header_data = apply_filters(
			'ms_view_membership_protectedcontent_header',
			array(
				'title' => __( 'Replace Menus', MS_TEXT_DOMAIN ),
				'desc' => __( 'Replace or protect WordPress menus.', MS_TEXT_DOMAIN ),
			),
			MS_Rule_ReplaceMenu::RULE_ID,
			array(
				'membership' => $this->data['membership'],
			),
			$this
		);

		ob_start();
		?>
		<div class="ms-settings">
			<?php
			MS_Helper_Html::settings_tab_header( $header_data );

			$listtable->views();
			$listtable->search_box();
			?>
			<form action="" method="post">
				<?php
				$listtable->display();

				do_action(
					'ms_view_membership_protectedcontent_footer',
					MS_Rule_ReplaceMenu::RULE_ID,
					$this
				);
				?>
			</form>
		</div>
		<?php

		MS_Helper_Html::settings_footer();

		return ob_get_clean();
	}

}